function addItems(){$("#add-item-error").hide();var n=$("#add-item-title").val(),t={title:n};$.post("todo/AddItem",t,function(){window.location="/todo"}).fail(function(n){if(n&&n.responseJSON){var t=n.responseJSON[Object.keys(n.responseJSON)[0]];$("#add-item-error").text(t).show()}})}function markCompleted(n){n.disabled=!0;var t={id:n.name};$.post("/Todo/MarkDone",t,function(){var t=n.parentElement.parentElement;$(t).addClas("done")})}$(document).ready(function(){$("#add-item-button").on("click",addItems);$(".done-checkbox").on("click",function(n){markCompleted(n.target)})});